import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import logo from './logo.png';
import {
  Container, Row, Navbar,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  Button,
  PopoverHeader,
  PopoverBody,
  Popover
} from 'reactstrap';
import Movies from './movie';

class App extends Component {
  render() {

    var MoviesData = [
      {
        name: "Alex à Barcelone",
        desc: "Il incarne quasiment le club à lui tout seul. Alex le prodige du FC Barcelone",
        img: "/pi.jpg"
      }, {
        name: "Lucifer",
        desc: "Il est chaud, il est boullant. Attention les filles, ça brule !",
        img: "/malefique.jpg"
      }, {
        name: "Les Aventures de Tintin",
        desc: "Parce qu'il achète la maquette d'un bateau appelé la Licorne, Tintin, un jeune r",
        img: "/tintin.jpg"
      },
      {
        name: "Les Aventures de Tintin",
        desc: "Parce qu'il achète la maquette d'un bateau appelé la Licorne, Tintin, un jeune r",
        img: "/tintin.jpg"
      },
      {
        name: "Les Aventures de Tintin",
        desc: "Parce qu'il achète la maquette d'un bateau appelé la Licorne, Tintin, un jeune r",
        img: "/tintin.jpg"
      },
    ];
    var MoviesList = MoviesData.map(function (movies, i) {
      return <Movies movieName={movies.name} movieDesc={movies.desc} movieImg={movies.img} movieNumber={i+1}/>;
    }) 

    var MoviesLike = [
      {
        name: "Alex à Barcelone",
        desc: "Il incarne quasiment le club à lui tout seul. Alex le prodige du FC Barcelone",
        img: "/pi.jpg"
      }, {
        name: "Lucifer",
        desc: "Il est chaud, il est boullant. Attention les filles, ça brule !",
        img: "/malefique.jpg"
      }, {
        name: "Les Aventures de Tintin",
        desc: "Parce qu'il achète la maquette d'un bateau appelé la Licorne, Tintin, un jeune r",
        img: "/tintin.jpg"
      }
    ];
    var moviesNameList = MoviesLike.map(function (movies, i) {
      return <Movies movieName={movies.name} movieDesc={movies.desc} movieImg={movies.img} movieNumber={i + 1}/>;
    }) 

    console.log(moviesNameList);
    var moviesCount = moviesNameList.length;
    var moviesLast = [];

    return (
      <div> 
        <Container>
          <MenuLogo/>
          <Row>
            {MoviesList}
          </Row>
        </Container>
      </div>
    );
  }
}


class MenuLogo extends Component {
  constructor(props) {
    super(props);

    this.toggle = this
      .toggle
      .bind(this);
    this.state = {
      popoverOpen: false
    };
  }

  toggle() {
    this.setState({
      popoverOpen: !this.state.popoverOpen
    });
  }
  render() {
    return (
      <div>
        <Navbar className="dark" dark expand="md">
          <NavbarBrand href="/"><img src={logo} alt="logo" /></NavbarBrand>
          <Nav className="ml-auto" navbar>
            <NavItem >
              <NavLink href="/components/">
                Last releases
              </NavLink>
            </NavItem>
            <NavItem >
              <NavLink href="https://github.com/reactstrap/reactstrap">
                My Movies
              </NavLink>
            </NavItem>
            <div>
              <Button id="Popover1" type="button" color="warning">
                11 Films
              </Button>
              <Popover
                placement="bottom"
                isOpen={this.state.popoverOpen}
                target="Popover1"
                toggle={this.toggle}>
                <PopoverHeader>Popover Title</PopoverHeader>
                <PopoverBody>{this.props.movieN}</PopoverBody>
              </Popover>
            </div>
          </Nav>
        </Navbar>

      </div>
    );
  }
}
export default App;
