import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import {Container, Row} from 'reactstrap';
import MenuLogo from './navbar';
import Movies from './movie';

class App extends Component {
  render() {
    var MoviesData = [
      {

        name: "L'Odyssée de Pi",
        desc: "Après que leur bateau est victime d'une violente tempête et coule au fond du Pac" +
            "ifique, un adolescent et un tigre du Bengale …",
        img: "/pi.jpg"
      }, {
        name: "Maléfique",
        desc: "Poussée par la vengeance et une volonté farouche de protéger les terres qu'elle " +
            "préside, Maléfique place ...",
        img: "/malefique.jpg"
      }, {
        name: "Les Aventures de Tintin",
        desc: "Parce qu'il achète la maquette d'un bateau appelé la Licorne, Tintin, un jeune r" +
            "eporter, se retrouve entraîné dans une fantastique aventure...",
        img: "/tintin.jpg"
      }
    ]
    var MoviesList = [];
    var MoviesList = MoviesList.map(function (movies, i) {
      return <Movies movieName={movies.name} movieDesc={movies.desc} movieImg={movies.img}/>;
    }) 

    return (
      <div>
        <Container>
          <MenuLogo/>
          <Row>
            {MoviesList}
          </Row>
        </Container>
      </div>
    );
  }
}

export default App;
