import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import logo from './logo.png';
import {
  Container, Row, Navbar,
  NavbarBrand,
  Nav,
  NavItem,
  NavLink,
  Button,
  PopoverHeader,
  PopoverBody,
  Popover,
  Card,
  CardImg,
  CardTitle,
  CardText,
  CardBody,
  Col
} from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
class App extends Component {
  constructor(props) {
    super(props);

    this.handleClickLikeOn = this.handleClickLikeOn.bind(this);
    this.handleClickLikeOff = this.handleClickLikeOff.bind(this);
    this.state = {
      viewLiked: false,
      moviesLiked: [],
    };
  }

  handleClickLikeOn() {
    this.setState({
      viewOnlyLike: true
    });
  }

  handleClickNoLiked() {
    this.setState({
      viewOnlyLike: false
    });
  }



  render() {

    var MoviesData = [
      {
        name: "Alex à Barcelone",
        desc: "Il incarne quasiment le club à lui tout seul. Alex le prodige du FC Barcelone",
        img: "/pi.jpg"
      }, {
        name: "Lucifer",
        desc: "Il est chaud, il est boullant. Attention les filles, ça brule !",
        img: "/malefique.jpg"
      }, {
        name: "Les Aventures de Tintin",
        desc: "Parce qu'il achète la maquette d'un bateau appelé la Licorne, Tintin, un jeune r",
        img: "/tintin.jpg"
      },
      {
        name: "Alex à Barcelone",
        desc: "Il incarne quasiment le club à lui tout seul. Alex le prodige du FC Barcelone",
        img: "/pi.jpg"
      }, {
        name: "Lucifer",
        desc: "Il est chaud, il est boullant. Attention les filles, ça brule !",
        img: "/malefique.jpg"
      }, {
        name: "Les Aventures de Tintin",
        desc: "Parce qu'il achète la maquette d'un bateau appelé la Licorne, Tintin, un jeune r",
        img: "/tintin.jpg"
      },

    ];
    var MoviesList = MoviesData.map(function (movies, i) {
      return <Movies movieName={movies.name} movieDesc={movies.desc} movieImg={movies.img} movieNumber={i + 1} />;
    }) 

    var moviesNameList = ["Lucifer", "Alex à Barcelone", "Les Aventures de Tintin", ]

    var moviesCount = moviesNameList.length;
    var moviesLast = moviesNameList.slice(-3);

    console.log(moviesLast);

    if (moviesCount === 0) {
      moviesLast = "aucun film sélectionné";
    } else if (moviesCount > 3) {
      moviesLast = moviesLast.join(', ') + '...';
        } else {
      moviesLast = moviesLast.join(', ') + '.';
    }

    return (
      <div> 
        <Container>
          <MenuLogo Count={moviesCount} Like={moviesLast} />
          <Row>
            {MoviesList}
          </Row>
        </Container>
      </div>
    );
  }
}


class MenuLogo extends Component {
  constructor(props) {
    super(props);

    this.toggle = this
      .toggle
      .bind(this);
    this.state = {
      popoverOpen: false
    };
  }

  toggle() {
    this.setState({
      popoverOpen: !this.state.popoverOpen
    });
  }
  render() {
    return (
      <div>
        <Navbar className="dark" dark expand="md">
          <NavbarBrand href="/"><img src={logo} alt="logo" /></NavbarBrand>
          <Nav className="ml-auto" navbar>
            <NavItem >
              <NavLink href="/" onClick={this.handleClickLikeOff} >
                Last releases
              </NavLink>
            </NavItem>
            <NavItem >
              <NavLink onClick={this.handleClickLikeOn}>
                My Movies
              </NavLink>
            </NavItem>
            <div>
              <Button id="Popover1" type="button" color="warning">
                {this.props.Count} nouveaux films
              </Button>
              <Popover
                placement="bottom"
                isOpen={this.state.popoverOpen}
                target="Popover1"
                toggle={this.toggle}>
                <PopoverHeader>Derniers films ajoutés</PopoverHeader>
                <PopoverBody>   {this.props.Like}  </PopoverBody>
              </Popover>
            </div>
          </Nav>
        </Navbar>

      </div>
    );
  }
}

class Movies extends Component {
  constructor() {
    super();
    this.handleClick = this
      .handleClick
      .bind(this);
    this.state = {
      liked: false
    };
  }

  handleClick() {
    console.log("click détécté");

    this.setState({
      liked: !this.state.liked
    });
  }

  render() {
    var color;
    if (this.state.liked) {
      color = {
        color: "#fc6861",
        position: 'absolute',
        right: '7%',
        fontSize: '30px',
        top: '3%',
        cursor: 'pointer'
      };
    } else {
      color = {
        position: 'absolute',
        right: '7%',
        top: '3%',
        color: '#fff',
        fontSize: '30px',
        cursor: 'pointer'
      }
    }

    return (

      <Col xs="12" md="4" sm="6" lg="4">
        <Card className="Card">
          <CardImg src={`..${this.props.movieImg}`} alt="Card image cap" />
          <FontAwesomeIcon style={color} icon={faHeart} onClick={this.handleClick} />
          <CardBody>
            <CardTitle className="title">{this.props.movieName}</CardTitle>
            <CardText>{this.props.movieDesc}</CardText>
          </CardBody>
        </Card>
      </Col>

    );
  }
}
export default App;
