import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Card, Button, CardImg, CardTitle, CardText, CardBody, Container, Row, Col } from 'reactstrap';
import './App.css';


class Movies extends Component {

  render() {
      Container.propTypes = {
          fluid: PropTypes.bool
          // applies .container-fluid class
      }
    return (
        <Container>
        <Row>
          <Card>    
            <CardImg src="https://lacapsule.academy/wks2/burger_1.png" alt="Burger" />
            <CardBody>
            <Col sm={8}>
              <CardTitle>Nom du Burger</CardTitle>
              <CardText>Description de la recette</CardText>
                </Col >
            <Col sm={8}>
              <Button>Buy</Button>
                </Col>
            </CardBody>
          </Card>
        </Row>
       </Container>    

      );
    }
  }

  export default Movies;
