import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import { Card, CardImg, CardTitle, CardText, CardBody, Container, Row, Col } from 'reactstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHeart } from '@fortawesome/free-solid-svg-icons';
import './App.css';


class Movies extends Component {

  render() { 

        var heart = {
            position : 'absolute',
            right : '20px',
            top: '20px',
            color: '#ed6464',
            fontSize: '34px'
        }

    return (

        <Col xs="4">
        <Card className="Card">
        <CardImg src="../malefique.jpg" alt="Card image cap" />
         <FontAwesomeIcon style={heart} icon={faHeart} />   
        <CardBody>
          <CardTitle className="title">Card Title</CardTitle>
          <CardText>This is a wider card with supporting text below as a natural lead-in to additional content. This content is a little bit longer.</CardText>
        </CardBody>
        </Card>
        </Col>



      );
    }
  }

  export default Movies;
