import React, {Component} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import './App.css';
import {Container, Row} from 'reactstrap';
import MenuLogo from './navbar';
import Movies from './movie';

class App extends Component {
  render() {

    var MoviesData = [
      {
        name: "Alex à Barcelone",
        desc: "Il incarne quasiment le club à lui tout seul. Alex le prodige du FC Barcelone",
        img: "/pi.jpg"
      }, {
        name: "Lucifer",
        desc: "Il est chaud, il est boullant. Attention les filles, ça brule !",
        img: "/malefique.jpg"
      }, {
        name: "Les Aventures de Tintin",
        desc: "Parce qu'il achète la maquette d'un bateau appelé la Licorne, Tintin, un jeune r",
        img: "/tintin.jpg"
      }
    ];
    var MoviesList = MoviesData.map(function (movies, i) {
      return <Movies movieName={movies.name} movieDesc={movies.desc} movieImg={movies.img} movieNumber={i+1}/>;
    }) 
    var moviesNameList = MoviesData.map(function (nameMovie, i) {
      return <MenuLogo movieName={nameMovie.name} movieNumber={i + 1} />;
    })
    var moviesCount = this.state.moviesNameList;

    return (
      <div>
        <Container>
          <MenuLogo />
          <Row>
            {MoviesList}
          </Row>
        </Container>
      </div>
    );
  }
}

export default App;
